mapper.select:
		select id,index_id,page from pfile where filingflag='0'

		 and left(id,8)>=#{startDate}



		and left(id,8)<=#{endDate}


		 and pid in (select index_id from treepinfo where  

           	 id like  #{x_idLike}

          and nodetype='1')

		order by tree_dliststr,list_id
