		)

mapper.select:
SELECT distinct E.*



     order by ${orderBy}


      order by E.INDEX_ID desc

mapper.sql:
		FROM TREEPINFO E LEFT JOIN (
			SELECT parent_id,COUNT(index_id) as childrenCount FROM TREEPINFO WHERE 1=1

				and NODETYPE = #{nodeType}


				and INTISUSE = #{intIsUse}

			GROUP BY parent_id
		) c on E.index_id=c.parent_id


		       1 = 1  
		   ${conditionString}

				and 

					E.ID like #{x_idLike}




			 	and E.projType <>#{projTypeNotEqual}



            	and bdate_s is not null



				and E.INDEX_ID = #{indexId}



			    and E.INDEX_ID IN

                  #{x_indexIds}




				and E.INDEX_ID <> #{indexIdNotEqual}



				and E.ID = #{id}



				and E.ID like #{idLike}



			    and E.ID IN

                  #{x_id}




				and E.NUM = #{num}



				and E.NUM like #{numLike}



			    and E.NUM IN

                  #{x_num}




				and E.TOP_ID = #{topId}



				and E.TOP_ID >= #{topIdGreaterThanOrEqual}



				and E.TOP_ID <= #{topIdLessThanOrEqual}



			    and E.TOP_ID IN

                  #{x_topId}





				and E.PARENT_ID = #{parentId}



			    and E.PARENT_ID IN

                  #{x_parentId}





				and E.INDEX_NAME = #{indexName}



				and E.INDEX_NAME like #{indexNameLike}



			    and E.INDEX_NAME IN

                  #{x_indexName}




				and E.NLEVEL = #{level}



				and E.NLEVEL >= #{levelGreaterThanOrEqual}



				and E.NLEVEL <= #{levelLessThanOrEqual}



			    and E.NLEVEL IN

                  #{x_level}





				and E.NODETYPE = #{nodeType}



				and E.NODETYPE like #{nodeTypeLike}



			    and E.NODETYPE IN

                  #{x_nodeType}




				and E.FROMID = #{fromId}



				and E.FROMID >= #{fromIdGreaterThanOrEqual}



				and E.FROMID <= #{fromIdLessThanOrEqual}



			    and E.FROMID IN

                  #{x_fromId}





